        R.drawable.sweethome,
        R.drawable.theglory
    )

    val listData: ArrayList<drama>
        get() {
            val list = arrayListOf<drama>()
            for (position in juduldrama.indices) {
                val drama = drama()
                drama.name = juduldrama[position]
                drama.detail = detaildrama[position]
                drama.photo = fotodrama[position]
                list.add(drama)
            }
            return list
        }
}